import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { summerContext } from '../../context/SummerCollection';

const Details = () => {
    const { getOneProduct, oneSummer } = useContext(summerContext)
    const [activeColor, setActiveColor] = useState("white");
    const params = useParams();
    useEffect(() => {
    getOneProduct(params.id);
  }, []);
    return oneSummer ? (
        <div className='main-div'>
            <div className='container'>
                <div>
                    <img src={oneSummer.img} alt="" />
                </div>
                <div>
                    <div>{oneSummer.title}</div>
                    <div>Артикул: {oneSummer.articul}</div>
                    <div className='colorful-circles'>
                        Цвет
                    <div className='circle-blue'></div>
                    <div className='circle-green'></div>
                    <div className='circle-marron'></div>
                    <div className='circle-brown'></div>
                    <div className='circle-purple'></div>
                    <div className='circle-white'></div>
                    <div className='circle-grey'></div>
                    <div className='circle-pink'></div>
                    </div>
                    <p>{oneSummer.price}</p>
                    <p>О товаре:</p>
                    <p>{oneSummer.text}</p>
                    <div>
                        <p>Размерный ряд: {oneSummer.size}</p>
                        <p>Состав ткани: {oneSummer.made}</p>
                    </div>
                    <div>
                        <p>Количество в линейки: {oneSummer.amount}</p>
                        <p>Материал: {oneSummer.material}</p>
                    </div>
                    <button>Добавить в корзину</button>
                </div>
            </div>

        </div>
    ): <h1>Loading</h1>
};

export default Details;
