import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import Pagination from '@mui/material/Pagination';
import PaginationItem from '@mui/material/PaginationItem';
import { useSearchParams } from 'react-router-dom';

const Collection = () => {
    const [searchParams, setSearchParams] = useSearchParams();
    const [page, setPage]=useState(
    searchParams.get("_page") ? searchParams.get("_page") : 1
    );
    const [totalColl, setTotalColl] = useState(0)
    const [collection, setCollection] = useState([])
    useEffect(() => {
    axios.get("http://localhost:8000/main-collection?_page=1&_limit=8" + window.location.search)
        .then(response => {
            setCollection(prev => [...prev, ...response.data])
            setTotalColl(Math.ceil(response.headers['x-total-count']/8))
         })
    }, [])
    useEffect(() => {
    setSearchParams({
        _page: page,
        _limit: 8,
    });
    }, [page]);

    useEffect(() => {
    axios.get("http://localhost:8000/main-collection")
        .then(response => {
            setCollection(prev => [...prev, ...response.data])
            setTotalColl(response.headers['x-total-count'])
         })
    }, [searchParams])
    const smallCard = {
        width: "286px",
        height: "374px",
        marginRight: "8px",
        marginBottom: "8px",
        width: "24%",
        position: "relative"
    }

    const smallCardPic = {
        width: "286px",
        height: "330px", 
    }
    return (
            <div className='container'>
                <h3 className='hit-main-title'>Хит продаж</h3>
                <div style={{display: "flex", flexWrap: "wrap"}}>
                    {collection.map((item) => (
                        <Card style={smallCard} key={item.id}>
                        <CardMedia style={smallCardPic}
                            component="img"
                            height="140"
                            image={item.img}
                            alt="image"
                        />
                            <div className='collection-words'>
                                <p>{item.title}</p>
                            </div>
                            <button className='collection-bottom'>Смотреть все
                                <ArrowForwardIosIcon sx={{ fill: '#fff'}} />
                            </button>
                    </Card>
                    ))}
            </div>
            <Pagination
                variant="outlined" shape="rounded"
                page={+page}
                count={totalColl}
                defaultPage={1}
                onChange={(page) => {
                setPage(page);
                page={page}
          }}
            />
            </div>
    );
};

export default Collection;
