import axios from "axios";
import React, { useEffect, useState } from "react";
import "./News.css"

const News = () => {
  const [photos, setPhotos] = useState([])
  cosnt[currentPage, setCurrentPage] = useState(1)
  const [fetching, setFetching] = useState(true)

  useEffect(() => {
    if (fetching) {
       axios.get("http://localhost:8000/news")
        .then(response => setPhotos(response.data))
    }
  })
  useEffect(() => {
    document.addEventListener("scroll", scrollHandler)
    return function () {
      document.removeEventListener("scroll", scrollHandler)
    }
  }, [])

  const scrollHandler = (e) => {
    console.log("scroll");
  }
  return (
    <div className="news-main-div">
      <div className="container">
        <p className="news-main-title">Новости</p>
        {photos.map((item) => (
        <div className="news-parts-outter">
            <div key={item.id} className="news-parts">
              <div className="news-part-image">
                <img height="100%" width="100%" src={item.img} alt="news-pic" />
              </div>
              <div className="news-part-text-outter">
                <div className="news-part-text">
                  <h4>{item.title}</h4>
                  <p>{item.text}</p>
                </div>
                <div className="news-little-rectangle"></div>
              </div>
            </div>
          </div>  
          ))}
      </div>
    </div>
  );
};

export default News;
