import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { cartContext } from '../../context/CartContext';

const Color = ({id}) => {
    const [colors, setColors] = useState([])
    const { isProdInCart, getColors } = useContext(cartContext)
    const [inCart, setInCart] = useState(isProdInCart(id))
    const [show, setShow] = useState(false)
    useEffect(() => {
      axios.get("http://localhost:8000/colors")
        .then(response => {
            setColors(prev => [...prev, ...response.data])
        })
    }, [])
    // console.log(inCart)
    // console.log(id);
    // console.log(inCart[0]?.item?.color);




    return (
        <div>
            <div className='colorful-circles'>
                {colors.map((item) => {
                    // console.log(item);
                    id == inCart[0]?.item?.id && inCart.map(i => {
                        i?.item.color == item.color && setShow(true)
                    })

                    // item.color == inCart
                    return <div key={item.id} style={{ backgroundColor: item.color }} className={ show ? "circle-circle" : "circle"}></div>
                })}
            </div>
        </div>
    );
};

export default Color;
