import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import axios from 'axios';

const Main = () => {
    const [hits, setHits] = useState([])
    const [brandnew, setBrandnew] = useState([])
    const [collection, setCollection] = useState([])

    useEffect(() => {
      axios.get("http://localhost:8000/hits")
        .then(response => {
           setHits(prev => [...prev, ...response.data])
         })
    }, [])
    useEffect(() => {
      axios.get("http://localhost:8000/brandnew")
        .then(response => {
           setBrandnew(prev => [...prev, ...response.data])
         })
    }, [])
    useEffect(() => {
      axios.get("http://localhost:8000/mini-collection")
        .then(response => {
           setCollection(prev => [...prev, ...response.data])
         })
    }, [])
    const cardStyle = {
        display: "flex",
        maxWidth: "286px", 
        max

};
    return (
        <div>
            {hits.map((item) => (
            <Card sx={{ maxWidth: 286}} key={item.id}>
                <CardActionArea>
                <CardMedia
                    component="img"
                    height="140"
                    image={item.image}
                    alt="hits image"
                />
                <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                    {item.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                    {item.price}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                    {item.size}
                </Typography>
                </CardContent>
                </CardActionArea>
            </Card>
            ))}
        </div>
    );
};

export default Main;
