import React, { useState } from 'react';
import CloseIcon from '@mui/icons-material/Close';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import Typography from '@mui/material/Typography';
import "./PhoneModal.css"
import { useNavigate } from 'react-router-dom';


const PhoneModal = () => {
    const [openDialog, setOpenDialog] = useState(true)
    const [success, setSuccess] = useState(false)
    const [name, setName] = useState("")
    const [phone, setPhone] = useState("")
    const navigate = useNavigate()
    function submit() {
        if (!name || !phone) {
            alert("Пожалуйста введите свои данные!")
        }
    }
    return (
        <div>
            <Dialog
            onClose={()=>setOpenDialog(false)}
            open={openDialog}
            >
                <div className='dialog-inner'>
                <CloseIcon onClick={() => setOpenDialog(false)} />
                <DialogContent>
                <h1 className='dialog-title'>Если у Вас остались вопросы</h1>
                <Typography>
                    <p className='dialog-text'>Оставьте заявку и мы обязательно Вам перезвоним</p>
                </Typography>
                <Typography>
                <input onChange={(e)=>setName(e.target.value)} className='input1 input' type="text" placeholder='Как к Вам обращаться?'/>
                </Typography>
                <Typography>
                    <input onChange={(e)=>setPhone(e.target.value)} className='input2 input' type="text" placeholder='Номер телефона'/>
                </Typography>
                    </DialogContent>
                <button className='dialog-button' onClick={()=>submit()}>
                    Заказать звонок
                </button>
            </div>
            </Dialog>
            <Dialog
            onClose={()=>setSuccess(false)}
            open={success}
            >
                <div className='dialog-inner'>
                <CloseIcon onClick={() => setOpenDialog(false)} />
                <DialogContent>

                <Typography>
                    <p className='dialog-text'>Оставьте заявку и мы обязательно Вам перезвоним</p>
                </Typography>
                <Typography>
                <input onChange={(e)=>setName(e.target.value)} className='input1 input' type="text" placeholder='Как к Вам обращаться?'/>
                </Typography>
                <Typography>
                    <input onChange={(e)=>setPhone(e.target.value)} className='input2 input' type="text" placeholder='Номер телефона'/>
                </Typography>
                    </DialogContent>
                <button className='dialog-button' onClick={()=>submit()}>
                    Заказать звонок
                </button>
            </div>
        </Dialog>
        </div>
    );
};

export default PhoneModal;
