import React, { useReducer } from "react";
import axios from "axios";

export const newContext = React.createContext();
const INIT_STATE = {
  new: [],
  newCount: 0,
};

const API = " http://localhost:8000/new";

const reducer = (state = INIT_STATE, action) => {
  switch (action.type) {
    case "GET_PRODUCTS":
      return {
        ...state,
        new: action.payload.data,
        newCount: Math.ceil(action.payload.headers["x-total-count"] / 12),
      };
    default:
      return state;
  }
};

const newContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, INIT_STATE);
  async function getNew() {
    let result = await axios(`${API}${window.location.search}`);
    dispatch({
      type: "GET_PRODUCTS",
      payload: result,
    });
  }
  return (
    <newContext.Provider
      value={{
        new: state.new,
        newCount: state.newCount,
        getNew,
      }}
    >
      {children}
    </newContext.Provider>
  );
};

export default newContextProvider;
