import React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import "./Favorite.css";
import { favoriteContext } from "../../context/favoriteContext";
import { CardActionArea } from "@mui/material";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }

    const smCardStyle = {
        width: "226px",
        height: "435px",
        marginRight: "8px",
        width: "20%", 
        marginBottom: "25px", 
        borderRadius: "none",
        position: "relative", 
        border: "none", 
        boxShadow:"none"
    }

    const smImageStyle = {
        width: "226px", 
        height: "332px"
    };

const Random = ({item}) => {
    return (
         <Card style={smCardStyle} key={item.id} square={true}>
            <CardActionArea>
                {inFav ? (
                    <FavoriteIcon
                    className='favorite'
                    style={{ color: "red" }}
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />
                ) : (
                    <FavoriteBorderIcon
                    style={{ color: "white" }}
                    className='favorite'
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />)}
                    <CardMedia
                        style={smImageStyle}
                        className="photos"
                        height="140"
                        component="img"
                        image={item.img}
                        alt="fav image"
                    />
                    <CardContent style={cardContent}>
                        <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                            {item.title}
                        </Typography>
                        <Typography style={priceStyle} className='hit-price' variant="body2" color="text.secondary">
                            {item.price}
                        </Typography>
                        <Typography style={sizeStyle} variant="body2">
                            Размер : {item.size}
                        </Typography>
                        <div className='colorful-circles'>
                            <div className='circle-blue'></div>
                            <div className='circle-green'></div>
                            <div className='circle-marron'></div>
                            <div className='circle-brown'></div>
                            <div className='circle-purple'></div>
                            <div className='circle-white'></div>
                            <div className='circle-grey'></div>
                            <div className='circle-pink'></div>
                        </div>
                    </CardContent>
                </CardActionArea>
                </Card>
    );
};

export default Random;
