import React from 'react';

const DetailsCard = ({ oneSummer }) => {
        const { addDelToFav, isProdInFav } = useContext(favoriteContext)
    const [inFav, setInFav] = useState(isProdInFav(item.id))
    return (
        <div className='main-div'>
            <div className='container'>
                <div className='main-div-inner'>
                    <div className='main-pics'>
                    <div className='main-div-pic'>
                        <img width="100%" src={oneSummer.img} alt="1" />
                    </div>
                    <div className='main-div-pic'>
                        <img width="100%" src={oneSummer.img4} alt="2" />
                    </div>
                    <div className='main-div-pic'>
                        <img width="100%" src={oneSummer.img3} alt="3" />
                    </div>
                    <div className='main-div-pic'>
                        <img width="100%" src={oneSummer.img2} alt="4" />
                    </div>
                    </div>
                <div className='details-info'>
                    <div className='details-info-inner'>
                        <div className='details-title'>{oneSummer.title}</div>
                        <div className='articul-name'><span className='artikul'>Артикул:</span>{oneSummer.artikul}</div>
                        <div className='details-circles'>
                        <p>Цвет</p>
                        <div className='circle-purple color'></div>
                        <div className='circle-white color'></div>
                        <div className='circle-grey color'></div>
                        <div className='circle-pink color'></div>
                        </div>
                        <p className='details-price'>{oneSummer.price}</p>
                        <p className='details-text-intro'>О товаре:</p>
                            <p className='details-text'>{oneSummer.text}</p>
                        <div className='details-bottom'> 
                            <div className='details-info-bottom'>
                                    <p><span>Размерный ряд: </span>{oneSummer.size}</p>
                                    <p><span>Количество в линейки:</span>{oneSummer.amount}</p>
                            </div>
                            <div className='details-info-bot'>
                                <p><span>Состав ткани: </span>{oneSummer.made}</p>
                                <p><span>Материал: </span>{oneSummer.material}</p>
                            </div>
                        </div>
                        <button className='details-button'>
                            <img style={{marginRight:"10px", marginBottom:"-3px"}} width="20px" src="https://cdn.discordapp.com/attachments/979601812472598619/980774983724830730/unknown.png" alt="cart" />
                                Добавить в корзину
                            </button>
                            <div>
                                 {inFav ? (
                                    <FavoriteIcon
                                    className='favorite'
                                    style={{ color: "red" }}
                                    onClick={() => {
                                    addDelToFav(oneSummer);
                                    setInFav(isProdInFav(oneSummer.id));
                                    }}
                                />
                                ) : (
                                    <FavoriteBorderIcon
                                    style={{ color: "white" }}
                                    className='favorite'
                                    onClick={() => {
                                    addDelToFav(oneSummer);
                                    setInFav(isProdInFav(oneSummer.id));
                                    }}
                                />
                                )}
                            </div>
                    </div>
                </div>
                </div>
            </div>

        </div>
    );
};

export default DetailsCard;
