import { Button } from '@mui/material';
import React, { useContext, useEffect } from 'react';
import { cartContext } from '../../context/CartContext';
import CloseIcon from '@mui/icons-material/Close';
import "./Cart.css"
const Cart = () => {
    const { getCart, cart, deleteFromCart, changeProductCount, cartLength} = useContext(cartContext);
  useEffect(() => {
    getCart();
  }, []);
    console.log(cart.totalPrice)
    return (
        <div className='main-div'>
            <div className='container'>
                {cart.products.length > 0 ? (
                <div> 
                        {cart.products.map((item) => (
                        <div key={item.item.id}>
                        <div className='cart-inner' >
                        <div className='cart-card'>
                            <div className='cart-card-inner'>
                                <div className='cart-pic'>
                                    <img width={112} src={item.item.img} alt="the main image" />
                                </div>
                                <div className='cart-info'>
                                    <h1 className='cart-title'>{item.item.title}</h1>
                                    <p className='cart-title'>{item.item.size}</p>
                                    <p className='cart-color'>Цвет</p>
                                    <p className='cart-price'>{item.item.price}</p>

                                <button className='count'
                                    onClick={() =>
                                    changeProductCount(item.count - 1, item.item.id)
                                    }
                                >
                                    -
                                </button>
                                                <input
                                                    style={{ width: "32px", background:"transparent", ooutline:"none", border:"none", textAlign:"center"}}
                                    type="number"
                                    disabled
                                    value={item.count}
                                />
                                <button className='count'
                                    onClick={() =>
                                    changeProductCount(item.count + 1, item.item.id)
                                    }
                                >
                                    +
                                </button>
                                <button className='cart-delete'>
                                        <CloseIcon onClick={()=>deleteFromCart(item.item.id)}/>
                                </button>
                                </div>
                            </div>
                        </div>
                        </div>
                       </div>
                        ))}
                        <div>
                            <div>
                            <h3>Сумма заказа</h3>
                            <p>Количество линеек: {cartLength} </p>
                            <p>Количество товаров: {cartLength * 5} </p>
                            <p>Стоимость: {cart.totalPrice}</p>
                            <p>Скидка</p>
                            <p>Итого к оплате</p>
                                <button>Оформить заказ</button>
                            </div>
                        </div>
                    </div>)  : null}
            </div>
        </div>
    );
};

export default Cart;
