import React, { useContext, useState } from 'react';
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import { favoriteContext } from '../../context/favoriteContext';

const NewCard = ({ item }) => {
    const { addDelToFav, isProdInFav } = useContext(favoriteContext)
    const [inFav, setInFav] = useState(isProdInFav(item.id))
    const discount = Math.ceil(item.price - (item.price * item.discount / 100))
    const cardStyle = {
        width: "226px",
        height: "435px",
        marginRight: "8px",
        width: "19%", 
        marginBottom: "64px", 
        position: "relative",
        border: "none", 
        boxShadow:"none"
    }
    const imageStyle = {
        width: "226px", 
        height: "330px"
    };

    const titleStyle = { 
        fontSize: "14px",
        fontWeight: "500",
        lineHeight: "17px",
        color: "#393939",
        margin: "0 0 6px 0"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontWeight: "500",
        fontSize: "13px", 
        margin: "0 0 16px 0",
        color: "#979797",
    }
    return (
        <Card style={cardStyle} key={item.id} square={true}>
            <CardActionArea>
                {inFav ? (
                    <FavoriteIcon
                        className='favorite'
                        style={{ color: "red" }}
                        onClick={() => {
                            addDelToFav(item);
                            setInFav(isProdInFav(item.id));
                        }}
                    />
                ) : (
                    <FavoriteBorderIcon
                        style={{ color: "white" }}
                        className='favorite'
                        onClick={() => {
                            addDelToFav(item);
                            setInFav(isProdInFav(item.id));
                        }}
                    />
                )}
            {item.discount ? <div className='red-discount'><span>{item.discount}%</span></div>: null}
            <CardMedia style={imageStyle}
                component="img"
                image={item.img}
                alt="image"
            />
            <CardContent style={cardContent}>
            {item.discount ?
                <div><span className='discount'>{discount} p</span><span className='price-discount'>{item.price} p</span></div> :  
                <Typography className='hit-price' variant="body2" color="text.secondary"><p className='discount'>{item.price} p</p>
                </Typography>
            }  
            <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                {item.title}
            </Typography>
            <Typography style={sizeStyle} variant="body2" color="text.secondary">
                Размер : {item.size}
            </Typography>
                <div className='colorful-circles'>
                    <div className='circle-blue'></div>
                    <div className='circle-green'></div>
                    <div className='circle-marron'></div>
                    <div className='circle-brown'></div>
                    <div className='circle-purple'></div>
                    <div className='circle-white'></div>
                    <div className='circle-grey'></div>
                    <div className='circle-pink'></div>
                </div>
            </CardContent>
            </CardActionArea>
        </Card>
    );
};

export default NewCard;
