import React, { useContext, useEffect } from 'react';
import { headerContext } from '../../context/HeaderContext';

const Header = () => {
    const { getHeader, header } = useContext(headerContext)
    useEffect(() => {
        getHeader()
    }, [])
    return (
        <div>
            {header.map((item) => (
                <div key={item.id}>
                    <img src={item.logo} alt="the logo" />
                    <p>{item.number}</p>
                </div>

            ))}
        </div>
    );
};

export default Header;
