import React, { useState } from "react";

function HelpComponen({ item, index}) {
  const [text, setText] = useState(null);

  const toggle = (index) => {
    if (text === index) {
      return setText(null)
    }
    setText(index)
  }
  return (
    <div className="help-outter-card" onClick={()=>toggle(index)} key={item.id}>
      <div className="help-inner-card">
        <h4>{item.title}</h4>
        {text === index ? (
          <img
            src={require("../../images/arrow-up.png")}
            alt="arrow up"
          />
        ) : (
          <img
            src={require("../../images/arrow-down.png")}
            alt="arrow down"
          />
        )}
      </div>
      {text ? <p className={text === index ? "help-extra-info" : "help-extra"}>{item.text}</p> : null}
    </div>
  );
}

export default HelpComponen;
