import React, {useState} from 'react';
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import "./Order.css"
import { Link } from 'react-router-dom';
import PhoneInput from 'react-phone-number-input';

const Order = () => {
    const [open, setOpen] = useState(true);
    const [phone, setPhone]=useState()
    const [order, setOrder] = useState({
        name: "", 
        surname: "", 
        email: "", 
        country: "", 
        city:""
    })
    function save() {
        if (
            !order.name || 
            !order.surname ||
            !order.email ||
            !order.country ||
            !order.city
        ) {
            alert("Please, fill in")
            return 
        } else {
            setOpen(false)
        }
    }

    function value(e) {
        let order = {
            [e.target.name]: [e.target.value]
        }
        setOrder(order)
    }

    const handleClose = () => {
    setOpen(false);
  };
    return (
        <div>
        <Dialog PaperProps={{ sx: { width: "480px", height:"710px" }}} open={open} onClose={handleClose}>
        <div className='dialog-inner'>
            <DialogContent>
            <h2>Оформление заказа</h2>
            <label className='order-label' htmlFor="name">Ваше имя</label>
            <br/>
            <input onChange={value} className='order-input' type="text" id='name' name='name' placeholder='Например Иван' />
            <br/>
            <label className='order-label'  htmlFor="surname">Ваше фамилия</label>
            <br/>
            <input onChange={value} className='order-input' type="text" placeholder='Например Иванов' />
            <br/>
            <label className='order-label'  htmlFor="email">Электронная почта</label>
            <br/>
            <input onChange={value} className='order-input' type="email" id='email' name='email' placeholder='example@mail.com' />
            <br/>
            <label className='order-label'  htmlFor="">Ваш номер телефона</label>
            <br/>
            <PhoneInput international value={ phone} onChange={setPhone} defaultCountry="KG" className='phone-input' placeholder="Введите номер телефона"/>
            <br/>
            <label className='order-label'  htmlFor="country">Страна</label>
            <br/>
            <input onChange={value} className='order-input' type="text" name='country' id='country' placeholder='Страна' />
            <br/>
            <label className='order-label'  htmlFor="city">Город</label>
            <br/>
            <input onChange={value} className='order-input' type="text" name='city' id='city' placeholder='Город' />
            <input className='order-checkbox' type="checkbox" />
            <label className='label'>Согласен с условиями <Link style={{ textDecoration: 'none', color: "#2F80ED", fontSize:"15px" }} to={'/offerta'}>публичной оферты</Link></label>
            <button onClick={()=>save()} className='order-button'>Заказать</button>
        </DialogContent>
        </div>
      </Dialog>
        </div>
    );
};

export default Order;
