import React, { useContext, useState } from 'react';
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import { favoriteContext } from '../../context/favoriteContext';
import { Link } from 'react-router-dom';
const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "24%", 
        marginBottom: "8px", 
        borderRadius: "none",
        position:"relative"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }
const BrandNew = ({ item }) => {
    const { addDelToFav, isProdInFav } = useContext(favoriteContext)
    const [inFav, setInFav] = useState(isProdInFav(item.id))
    console.log(item.discount)
    console.log(item.price)
    return (
       <Card style={cardStyle} square={true}>
            <CardActionArea>
                {inFav ? (
                <FavoriteIcon
                className='favorite'
                style={{ color: "red" }}
                onClick={() => {
                  addDelToFav(item);
                  setInFav(isProdInFav(item.id));
                }}
              />
            ) : (
                <FavoriteBorderIcon
                style={{ color: "white" }}
                className='favorite'
                onClick={() => {
                  addDelToFav(item);
                  setInFav(isProdInFav(item.id));
                }}
              />
                )}
                {item.discount ? <div>{item.discount}</div>: null}
                <Link to={`/newdetails/${item.id}`}>
                <CardMedia style={imageStyle}
                    component="img"
                    height="140"
                    image={item.img}
                    alt="hits image"
                    />
                </Link>
                <CardContent style={cardContent}>
                    <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                        {item.title}
                    </Typography>
                    <Typography style={priceStyle}  className='hit-price' variant="body2" color="text.secondary">
                        {item.discount ? <div><p className='discount'>{(item.price - (item.price * discount)) / 100}</p><p className='price-discount'>{item.price}</p></div> : item.price}
                    </Typography>
                    <Typography style={sizeStyle} variant="body2" color="text.secondary">
                        Размер : {item.size}
                    </Typography>
                    <div className='colorful-circles'>
                        <div className='circle-blue'></div>
                        <div className='circle-green'></div>
                        <div className='circle-marron'></div>
                        <div className='circle-brown'></div>
                        <div className='circle-purple'></div>
                        <div className='circle-white'></div>
                        <div className='circle-grey'></div>
                        <div className='circle-pink'></div>
                    </div>
                    </CardContent>
                </CardActionArea>
            </Card>
    );
};

export default BrandNew;
