import React from 'react';

const SimiliarCard = () => {
    return (
        <Card sx={{width:"226px", height: "420px"}} key={item.id} square={true}>
                            <CardActionArea>
                        {inFav ? (
                            <FavoriteIcon
                            className='favorite'
                            style={{ color: "red" }}
                            onClick={() => {
                            addDelToFav(item);
                            setInFav(isProdInFav(item.id));
                            }}
                        />
                        ) : (
                            <FavoriteBorderIcon
                            style={{ color: "white" }}
                            className='favorite'
                            onClick={() => {
                            addDelToFav(item);
                            setInFav(isProdInFav(item.id));
                            }}
                        />
                            )}
                        {item.discount ? <div className='red-discount'><span>{item.discount}%</span></div>: null}
                        <Link to={`/details/${item.id}`} >
                            <CardMedia sx={{height:"332px"}}
                                component="img"
                                height="140"
                                image={item.img}
                                alt="hits image"
                            />
                        </Link>
                            <CardContent>
                            <Typography gutterBottom variant="h5" component="div">
                                    {item.title}
                                </Typography>
                            {item.discount ?
                            <div><span className='discount'>{discount} p</span><span className='price-discount'>{item.price} p</span></div> :  
                            <Typography className='hit-price' variant="body2" color="text.secondary"><p className='discount'>{item.price} p</p>
                            </Typography>
                            }  
                            <Typography variant="body2" color="text.secondary">
                                    Размер : {item.size}
                            </Typography>
                                <Color/>
                            </CardContent>
                        </CardActionArea>
                </Card>
    );
};

export default SimiliarCard;
