import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import axios from 'axios';
import "./Main.css"

const Main = () => {
    const [hits, setHits] = useState([])
    const [fetching, setFetching] = useState(true)
    const [brandnew, setBrandnew] = useState([])
    const [collection, setCollection] = useState([])
    const [currentPage, setCurrentPage] = useState(1)
    const [totalCount, setTotalCount] = useState(0)
    useEffect(() => {
    if (fetching) {
      axios.get(`http://localhost:8000/hits?_limit=8&_page=${currentPage}`)
        .then(response => {
            setHits(prev => [...prev, ...response.data])
            setCurrentPage(prevState => prevState + 1)
             setTotalCount(response.headers['x-total-count'])
         })
        .finally(()=>setFetching(false))
    }
  }, [fetching])

    // useEffect(() => {
    //   axios.get("http://localhost:8000/brandnew")
    //     .then(response => {
    //        setBrandnew(prev => [...prev, ...response.data])
    //      })
    // }, [])
    // useEffect(() => {
    //   axios.get("http://localhost:8000/hits?_limit=8")
    //     .then(response => {
    //        setCollection(response.data)
    //      })
    // }, [])
    const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "23%", 
        marginBottom: "8px"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }
    const clickHandler = () => {
        if (hits.length < totalCount) {
            setFetching(true)
        }
    }
    return (
        <div className='container'>
            <h3 className='hit-main-title'>Хит продаж</h3>
            <div style={{display: "flex", flexWrap: "wrap" ,}}>
                {hits.map((item) => (
                <Card style={cardStyle} key={item.id}>
                    <CardActionArea>
                    <CardMedia style={imageStyle}
                        component="img"
                        height="140"
                        image={item.image}
                        alt="hits image"
                    />
                    <CardContent>
                    <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                        {item.title}
                    </Typography>
                    <Typography style={priceStyle}  className='hit-price' variant="body2" color="text.secondary">
                        {item.price}
                    </Typography>
                    <Typography style={sizeStyle} variant="body2" color="text.secondary">
                        Размер : {item.size}
                    </Typography>
                        <div>
                            <div></div>
                            <div></div>
                            <div></div>
                            <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                        </div>
                    </CardContent>
                    </CardActionArea>
                </Card>
                ))}
            </div>
            <div className='hit-button-div'>
                <button className='hit-button' onClick={clickHandler}>Еще</button>
            </div>
        </div>
    );
};

export default Main;
