import React, { useEffect, useState } from 'react';
import { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { allContext } from '../../context/AllContext';
import SearchIcon from '@mui/icons-material/Search';
import "./Search.css"


const Search = () => {
    const { getAllProducts, data } = useContext(allContext)
    useEffect(() => {
        getAllProducts()
    }, [])
    const allData = data.reduce((acc, val) => acc.concat(val), [])
    const [filteredData, setFilteredData] = useState([])
    const [searchValue, setSearchValue] = useState("")
    const navigate = useNavigate()

    const handleFilter = (event) => {
        const searchWord = event.target.value;
        setSearchValue(searchWord);
        const newFilter = allData.filter((value) => {
            return value.title.toLowerCase().includes(searchWord.toLowerCase());
        });

        if (searchWord === "") {
            setFilteredData([]);
        } else {
            setFilteredData(newFilter);
        }
    };

    const clearInput = () => {
        setFilteredData([]);
        setSearchValue("");
    };

    const handleNavigate = () => {
        navigate(`/searchpage/${searchValue}`)
        clearInput()
    }
    return (
        <>
            <div className='search'>
                <div className='input-icon'>
                    <input value={searchValue} onChange={handleFilter} className='input-field' style={{ background: "#F8F8F8", border: "0.3px solid #E0E0E0" }} placeholder='Поиск' type="text" />
                    <div className='search-icon'>
                            <SearchIcon onClick/>
                    </div>
                </div>
            </div>
            {filteredData.length != 0 && (
                <div className="dataResult">
                    {filteredData.map((value, key) => (
                        <Link to={`/searchpage/${value.title}`} >
                            {value.title}
                        </Link>
                    ))}
                </div>


            )
            }
        </>
    );
}

export default Search;
