import React, { useContext, useState, useEffect } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { favoriteContext } from "../../context/favoriteContext";
import { CardActionArea } from "@mui/material";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import { useNavigate, useParams } from 'react-router-dom';
import Color from '../Colors/Color';
import { summerContext } from '../../context/SummerCollection';

const Random = ({ item }) => {
    const discount = Math.ceil(item.price - (item.price * item.discount / 100))
    const { addDelToFav, isProdInFav } = useContext(favoriteContext)
    const {getOneProduct} = useContext(summerContext)
    const [inFav, setInFav] = useState(isProdInFav(item.id))
    const navigate = useNavigate()
    const { id } = useParams()
    const [pic, setPic] = useState(item.img)
    const [hover, setHover] = useState("sm-hover")

    const handleMouse = (event) => {
        var mouseX = event.nativeEvent.offsetX; 
        let cardWidth = event.target.clientWidth
        let specWidth = Math.ceil(cardWidth / 4)

        if (mouseX > 1 && mouseX < specWidth) {
            setPic(item.img)
            setHover("sm-hover-1")
        }if (mouseX > specWidth && mouseX < specWidth * 2) {
            setPic(item.img2)
            setHover("sm-hover-2")
        } else if (
            mouseX > specWidth * 2 && mouseX < specWidth * 3) {
            setPic(item.img3)
            setHover("sm-hover-3")
        } else if (
            mouseX > specWidth * 3 && mouseX < specWidth * 4) {
            setPic(item.img4)
            setHover("sm-hover-4")
             }
    }
    const handleLeave = () => {
        setPic(item.img)
        setHover("hover")
    }
    useEffect(() => {
        getOneProduct(id)
    }, [id])

    const cardStyle = {
        width: "226px", 
        height: "435px", 
        marginRight: "8px", 

    }
    return (
         <Card onMouseMove = {(e) => handleMouse(e)} onMouseLeave={()=>handleLeave()} onClick={()=>navigate(`/details/${item.id}`)} sx={{width:"226px", height: "435px", marginRight:"8px", border:"none", backgroundColor:"transparent", color:"rgba(0, 0, 0, 0)", boxShadow:"none"}} key={item.id} square={true}>
            <CardActionArea>
                {inFav ? (
                    <FavoriteIcon
                    className='favorite'
                    style={{ color: "red" }}
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />
                ) : (
                    <FavoriteBorderIcon
                    style={{ color: "white" }}
                    className='favorite-hover'
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />)}
                    <CardMedia
                    sx={{height:"332px"}}
                        className="photos"
                        height="140"
                        component="img"
                        image={pic}
                        alt="fav image"
                    />
                    <div className={hover}></div>
                    <CardContent>
                         <Typography gutterBottom variant="h5" component="div">
                                {item.title}
                        </Typography>
                            {item.discount ?
                        <div><span className='discount'>{discount} p</span><span className='price-discount'>{item.price} p</span></div> :  
                                <Typography className='hit-price' variant="body2" color="text.secondary"><p className='discount'>{item.price} p</p>
                                </Typography>
                            }   
                            <Typography variant="body2" color="text.secondary">
                                Размер : {item.size}
                        </Typography>
                        <Color/>
                    </CardContent>
                </CardActionArea>
                </Card>
    );
};

export default Random;
