import axios from 'axios';
import React, { useEffect, useState } from 'react';
import "./Help.css"

const Help = () => {
    const [help, setHelp] = useState([])
    const [image, setImage] = useState([])
    const [text, setText] = useState([])
    const [showFirstText, setShowFirstText] = useState(false)
    // const [showSecondText, setShowSecondText] = useState(false)
    // const [showThirdText, setShowThirdText] = useState(false)
    // const [showFourthText, setShowFourthText] = useState(false)
    // const [showFifthText, setShowFifthText] = useState(false)
    // const [showSixthText, setShowSixthText] = useState(false)
    // const [showSeventhText, setShowSeventhText] = useState(false)
      useEffect(() => {
      axios.get("http://localhost:8000/help")
        .then(response => {
           setHelp(prev => [...prev, ...response.data])
         })
      }, [])
    useEffect(() => {
      axios.get("http://localhost:8000/image")
        .then(response => {
           setImage(response.data)
         })
    }, [])
  useEffect(() => {
      axios.get("http://localhost:8000/paragraph")
        .then(response => {
           return response
         })
  }, [])
    return (
        <div className='help-main-div'>
            <div className='container'>
                <div className='help-inner-div'>
                    {image.map((item) => (
                        <div className='help-inner-image' key={item.id}>
                        <img width="500px" height="500px" src={item.img} alt="help-main-pic" />
                    </div>
                    ))}
                    <div className='help-inner-info'>
                        <h3>Помощь</h3>
                        {help.map((item) => (
                            <div className='help-outter-card'key={item.id}>
                                <div className='help-inner-card'>
                                    <h4>{item.title}</h4>
                                    {showFirstText ? <img onClick={() => setShowFirstText(false)} src={require('../../images/arrow-up.png')} alt="arrow up" /> : <img onClick={() => setShowFirstText(true)} src={require('../../images/arrow-down.png')} alt="arrow down" />}
                                </div>
                                    {showFirstText ? <p className='help-extra-info'>{item.text}</p> : null}
                            </div>
                            ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Help;
