import axios from "axios";
import React, { useEffect, useState } from "react";
import "./News.css"

const News = () => {
  const [photos, setPhotos] = useState([])
  const[currentPage, setCurrentPage] = useState(1)
  const [fetching, setFetching] = useState(true)
  const [totalCount, setTotalCount]=useState(0)

  useEffect(() => {
    if (fetching) {
      axios.get(`http://localhost:8000/news?_limit=8&_page=${currentPage}`)
        .then(response => {
           setPhotos(prev => [...prev, ...response.data])
           setCurrentPage(prevState => prevState + 1)
           setTotalCount(response.headers['x-total-count'])
         })
        .finally(()=>setFetching(false))
    }
  }, [fetching])

  useEffect(() => {
    document.addEventListener("scroll",scrollHandler)
    return function () {
        document.removeEventListener("scroll", scrollHandler)
    }
  }, [fetching])

  const scrollHandler = (e) => {
    if (e.target.documentElement.scrollHeight - (e.target.documentElement.scrollTop + window.innerHeight) < 100 && photos.length < totalCount) {
      setFetching(true)
    }
  }
  return (
    <div className="news-main-div">
      <div className="container">
        <p className="news-main-title">Новости</p>
        {photos.map((item) => (
        <div className="news-parts-outter" key={item.id} >
            <div className="news-parts">
              <div className="news-part-image">
                <img height="100%" width="100%" src={item.img} alt="news-pic" />
              </div>
              <div className="news-part-text-outter">
                  <h4>{item.title}</h4>
                <input type="checkbox" className="read-more-checker" id="read-more-checker" checked="checked" />
                  <div className="news-part-text">
                    <p>{item.text}</p>
                    {/* <div class="bottom"></div> */}
                  </div>
                  <label for="read-more-checker" className="read-more-button read-more-checker "></label>
                <div className="news-little-rectangle"></div>
              </div>
            </div>
          </div>  
          ))}
      </div>
    </div>
  );
};

export default News;
