import axios from "axios";
import React, { useEffect, useState } from "react";
import "./News.css"

const News = () => {
  const [photos, setPhotos] = useState([])

  useEffect(() => {
    axios.get("http://localhost:8000/news")
      .then(response => setPhotos(response.data))
  })
  useEffect(() => {
    document.addEventListener("scroll", scrollHandler)
    return function () {
      document.removeEventListener("scroll", scrollHandler)
    }
  }, [])

  const scrollHandler = (e) => {
    console.log("scroll");
  }
  return (
    <div className="news-main-div">
      <div className="container">
        <p className="news-main-title">Новости</p>
        <div className="news-parts-outter">
          {photos.map((item) => (
            <div key={item.id} className="news-parts">
              <div className="news-part-image">
                <img width="226px" src={item.img} alt="news-pic" />
              </div>
            <div className="news-part-text">
                <h4>{item.title}</h4>
                <p>{item.text}</p>
            </div>
          </div>
          ))}
        </div>  
      </div>
    </div>
  );
};

export default News;
