import React from "react";

function HelpComponen() {
  return (
    <div className="help-outter-card" key={item.id}>
      <div className="help-inner-card">
        <h4>{item.title}</h4>
        {showFirstText ? (
          <img
            onClick={() => setShowFirstText(false)}
            src={require("../../images/arrow-up.png")}
            alt="arrow up"
          />
        ) : (
          <img
            onClick={() => setShowFirstText(true)}
            src={require("../../images/arrow-down.png")}
            alt="arrow down"
          />
        )}
      </div>
      {showFirstText ? <p className="help-extra-info">{item.text}</p> : null}
    </div>
  );
}

export default HelpComponen;
