import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { cartContext } from '../../context/CartContext';

const Color = ({id, color}) => {
    const [colors, setColors] = useState([])
    const { isProdInCart, getColors } = useContext(cartContext)
    const [inCart, setInCart] = useState(isProdInCart(id, color))
    useEffect(() => {
      axios.get("http://localhost:8000/colors")
        .then(response => {
            setColors(prev => [...prev, ...response.data])
         })
    }, [])
    return (
        <div>
            <div className='colorful-circles'>
                {colors.map((item) => (
                        inCart && item.color == color ?(<div key={item.id} className='border-circles'><div key={item.id} style={{backgroundColor: item.color}} className='circle'></div>
                            </div>
                        ) : (<div key={item.id} style={{backgroundColor: item.color}} className='circle'></div>)
            ))}
            </div>
        </div>
    );
};

export default Color;
