import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import "./Favorite.css";
import { favoriteContext } from "../../context/favoriteContext";
import { CardActionArea } from "@mui/material";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";

const Favorite = () => {
    const { fav, getFav, deleteProdInFav } = useContext(favoriteContext)
    const { addDelToFav, isProdInFav } = useContext(favoriteContext)
  useEffect(() => {
    getFav();
  }, []);
    const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "24%", 
        marginBottom: "8px", 
        borderRadius: "none",
        position:"relative"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }
    return (
        <div className="fav-div">
            <div className="container">
                <h3 className='fav-main-title'>Избранное</h3>
                <p className="fav-main-text">Товары в избранном 16</p>
                <div style={{display: "flex"}}>
                    {fav?.products.length > 0 ? (
                    fav.products.map((item1) => (
                        <Card style={cardStyle} key={item1.id} square={true}>
                            <CardActionArea>
                                {inFav ? (
                                    <FavoriteIcon
                                    className='favorite'
                                    style={{ color: "red" }}
                                    onClick={() => {
                                    addDelToFav(item1);
                                    setInFav(isProdInFav(item1.id));
                                    }}
                                />
                                ) : (
                                    <FavoriteBorderIcon
                                    style={{ color: "white" }}
                                    className='favorite'
                                    onClick={() => {
                                    addDelToFav(item1);
                                    setInFav(isProdInFav(item1.id));
                                    }}
                                />
                                )}
                                <CardMedia
                                style={imageStyle}
                                className="photos"
                                height="140"
                                component="img"
                                image={item1.item.image}
                                alt="fav image"
                            />
                            <CardContent style={cardContent}>
                                <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                                            {item1.item.title}
                                </Typography>
                                <Typography style={priceStyle}  className='hit-price' variant="body2" color="text.secondary">
                                    {item1.item.price}
                                </Typography>
                                <Typography style={sizeStyle} variant="body2">
                                    Размер : {item1.item.size}
                                </Typography>
                                <div className='colorful-circles'>
                                    <div className='circle-blue'></div>
                                    <div className='circle-green'></div>
                                    <div className='circle-marron'></div>
                                    <div className='circle-brown'></div>
                                    <div className='circle-purple'></div>
                                    <div className='circle-white'></div>
                                    <div className='circle-grey'></div>
                                    <div className='circle-pink'></div>
                                </div>
                                </CardContent>
                            </CardActionArea>
                        </Card>
                    ))
                    ) : (
                    <h1>No products</h1>
                    )}
                </div>
            </div>
        </div>

    );
};

export default Favorite;
