import React, { useContext, useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { favoriteContext } from "../../context/favoriteContext";
import { CardActionArea } from "@mui/material";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import { useNavigate } from 'react-router-dom';

const Random = ({ item }) => {
    const navigate = useNavigate()
    const { addDelToFav, isProdInFav } = useContext(favoriteContext)
    const [inFav, setInFav] = useState(isProdInFav(item.id))
    return (
         <Card onClick={()=>navigate("/details/:id")} key={item.id} square={true}>
            <CardActionArea>
                {inFav ? (
                    <FavoriteIcon
                    className='favorite'
                    style={{ color: "red" }}
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />
                ) : (
                    <FavoriteBorderIcon
                    style={{ color: "white" }}
                    className='favorite'
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />)}
                    <CardMedia
                        className="photos"
                        height="140"
                        component="img"
                        image={item.img}
                        alt="fav image"
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">
                            {item.title}
                        </Typography>
                        <Typography className='hit-price' variant="body2" color="text.secondary">
                            {item.price}
                        </Typography>
                        <Typography variant="body2">
                            Размер : {item.size}
                        </Typography>
                        <div className='colorful-circles'>
                            <div className='circle-blue'></div>
                            <div className='circle-green'></div>
                            <div className='circle-marron'></div>
                            <div className='circle-brown'></div>
                            <div className='circle-purple'></div>
                            <div className='circle-white'></div>
                            <div className='circle-grey'></div>
                            <div className='circle-pink'></div>
                        </div>
                    </CardContent>
                </CardActionArea>
                </Card>
    );
};

export default Random;
