import { useContext } from 'react';
import { allContext } from '../../context/AllContext';

const Search = () => {
    const { getAllProducts, data } = useContext(allContext)
    useEffect(() => {
        getAllProducts()
    }, [])
    const allData = data.reduce((acc, val) => acc.concat(val), [])
    const [filteredData, setFilteredData] = useState([])
    const [searchValue, setSearchValue] = useState("")
     const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.title.toLowerCase().includes(searchWord.toLowerCase());
    });

    if (searchWord === "") {
      setFilteredData([]);
    } else {
      setFilteredData(newFilter);
    }
  };

  const clearInput = () => {
    setFilteredData([]);
    setWordEntered("");
  };
    return (
        <div className='search'>
            <div className='input-icon'>
                <span className='icon'>
                    <img width="35px" height="35px" src={require('../../images/small-header.png')} alt="search" />
                </span>
                    <input onChange={handleFilter} className='input-field' style={{background: "#F8F8F8", border: "0.3px solid #E0E0E0"}}placeholder='Поиск' type="text" />
            </div>
        </div>
    );
};

export default Search;
