import React, {useState} from 'react';
import Dialog from '@mui/material/Dialog';
import DialogContent from '@mui/material/DialogContent';
import "./Order.css"
import { Link } from 'react-router-dom';

const Order = () => {
    const [open, setOpen] = useState(true);

    const handleClose = () => {
    setOpen(false);
  };
    return (
        <div>
        <Dialog PaperProps={{ sx: { width: "440px" }}} open={open} onClose={handleClose}>
        <div className='dialog-inner'>
            <DialogContent>
            <h2>Оформление заказа</h2>
            <label className='order-label' htmlFor="name">Ваше имя</label>
            <br/>
            <input className='order-input' type="text" id='name' name='name' placeholder='Например Иван' />
            <br/>
            <label className='order-label'  htmlFor="surname">Ваше фамилия</label>
            <br/>
            <input className='order-input' type="text" placeholder='Например Иванов' />
            <br/>
            <label className='order-label'  htmlFor="email">Электронная почта</label>
            <br/>
            <input className='order-input' type="email" id='email' name='email' placeholder='example@mail.com' />
            <br/>
            <label className='order-label'  htmlFor="">Ваш номер телефона</label>
            <br/>
            <input className='order-input' type="text" />
            <br/>
            <label className='order-label'  htmlFor="country">Страна</label>
            <br/>
            <input className='order-input' type="text" name='country' id='country' placeholder='Страна' />
            <br/>
            <label className='order-label'  htmlFor="city">Город</label>
            <br/>
            <input className='order-input' type="text" name='city' id='city' placeholder='Город' />
            <input className='order-checkbox' type="checkbox" />
            <label className='label'>Согласен с условиями <Link style={{ textDecoration: 'none', color: "#2F80ED", fontSize:"15px" }} to={'/offerta'}>публичной оферты</Link></label>
            <button className='order-button'>Заказать</button>
        </DialogContent>
        </div>
      </Dialog>
        </div>
    );
};

export default Order;
