import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Hits from '../Hits/Hits';
import HitDetailsCard from './HitDetailsCard';

const HitDetails = () => {
    const [hit, setHit] = useState(null)
    const [hits, setHits] = useState([])
    const params = useParams();
    useEffect(() => {
        axios.get("http://localhost:8000/hits")
            .then(response => {
            setHits([prev => [...prev, ...response.data]])
        })
    }, [])
        useEffect(() => {
        axios.get(`http://localhost:8000/hits/${params.id}`)
            .then(response => {
            setHit(response.data)
        })  
    }, [])
    return (
        <div style={{backgroundColor: "#ECECEC"}}>
            {hit ? <HitDetailsCard id={params.id} item={hit} /> : <h1>Loading</h1>}
            <div className='container'>
                <p className='extra-products'>Похожие товары</p>
                <div style={{display:"flex"}}>
                    {hits.slice(0, 5).map((item) => (
                        <Hits item={item} key={item.id} />
                ))}
                </div>
            </div>

        </div>
    );
};

export default HitDetails;
