import React, { useContext, useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import axios from 'axios';
import "./Main.css"
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';

const Main = () => {
    const [hits, setHits] = useState([])
    const [fetching, setFetching] = useState(true)
    const [fetch, setFetch] = useState(true)
    const [brandnew, setBrandnew] = useState([])
    const [fetchCollection, setFetchCollection] = useState(true)
    const [collection, setCollection] = useState([])
    const [currentPage, setCurrentPage] = useState(1)
    const [page, setPage]=useState(1)
    const [totalCount, setTotalCount] = useState(0)
    const [totalNew, setTotalNew] = useState(0)
    const [pageColl, setPageColl]=useState(1)
    const [totalColl, setTotalColl] = useState(0)
    const [pluses, setPluses] = useState([])
     const { addProductToFavorite, checkItemInFavorite } =useContext(favoriteContext);
    useEffect(() => {
    if (fetching) {
      axios.get(`http://localhost:8000/hits?_limit=8&_page=${currentPage}`)
        .then(response => {
            setHits(prev => [...prev, ...response.data])
            setCurrentPage(prevState => prevState + 1)
             setTotalCount(response.headers['x-total-count'])
         })
        .finally(()=>setFetching(false))
    }
    }, [fetching])

    useEffect(() => {
    if (fetch) {
      axios.get(`http://localhost:8000/brandnew?_limit=4&_page=${page}`)
        .then(response => {
            setBrandnew(prev => [...prev, ...response.data])
            setPage(prevState => prevState + 1)
            setTotalNew(response.headers['x-total-count'])
         })
        .finally(()=>setFetch(false))
    }
    }, [fetch])


    useEffect(() => {
    if (fetchCollection) {
      axios.get(`http://localhost:8000/collection?_limit=4`)
        .then(response => {
            setCollection(prev => [...prev, ...response.data])
            setPageColl(prevState => prevState + 1)
            setTotalColl(response.headers['x-total-count'])
         })
        .finally(()=>setFetchCollection(false))
    }
    }, [fetchCollection])


     useEffect(() => {
      axios.get("http://localhost:8000/pluses")
        .then(response => {
            setPluses(prev => [...prev, ...response.data])
         })
    }, [])


    const clickHandler = () => {
        if (hits.length < totalCount) {
            setFetching(true)
        }
    }
    const clickNew = () => {
        if (brandnew.length < totalNew) {
            setFetch(true)
        }
    }
    const clickColl = () => {
        if (collection.length < totalColl) {
            setFetchCollection(true)
        }
    }
    const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "24%", 
        marginBottom: "8px", 
        borderRadius: "none",
        position:"relative"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }

    const smallCard = {
        width: "286px",
        height: "374px",
        marginRight: "8px",
        width: "24%",
        position: "relative"
    }

    const smallCardPic = {
        width: "286px",
        height: "330px", 
    }
    return (
        <div className='main'>
            <div className='container'>
