import { Button } from '@mui/material';
import React, { useContext, useEffect } from 'react';
import { cartContext } from '../../context/CartContext';
import CloseIcon from '@mui/icons-material/Close';

const Cart = () => {
    const { getCart, cart, deleteFromCart, changeProductCount, cartLength} =useContext(cartContext);
  useEffect(() => {
    getCart();
  }, []);
    return (
        <div className='main-div'>
            <div className='container'>
                {cart.products.length > 0 ? (
                <>
                        {cart.products.map((item) => (
                        <>
                         <div className='cart-inner'>
                        <div className='cart-card'>
                            <div className='cart-card-inner'>
                                <div className='cart-pic'>
                                    <img src={item.item.img} alt="the main image" />
                                </div>
                                <div className='cart-info'>
                                    <h1>{item.item.title}</h1>
                                    <p>{item.item.size}</p>
                                    <p>{item.item.price}</p>
                                    </div>
                                <Button
                                    onClick={() =>
                                    changeProductCount(item.count - 1, item.item.id)
                                    }
                                >
                                    -
                                </Button>
                                    <input
                                    type="number"
                                    style={{ width: "30%" }}
                                    disabled
                                    value={item.count}
                                />
                                <Button
                                    onClick={() =>
                                    changeProductCount(item.count + 1, item.item.id)
                                    }
                                >
                                    +
                                </Button>
                                <Button>
                                        <CloseIcon onClick={()=>deleteFromCart(item.item.id)}/>
                                </Button>

                            </div>
                        </div>
                        </div>
                        <div>
                            <h3>Сумма заказа</h3>
                            <p>Количество линеек: {cartLength} </p>
                            <p>Количество линеек: {cartLength * 5} </p>
                            <p>Стоимость: {cart.totalPrice}</p>
                            <p>Скидка</p>
                            <p>Итого к оплате</p>
                            <button>Оформить заказ</button>
                        </div>
                       </>
                    ))}
                    </>)  : null}
            </div>
        </div>
    );
};

export default Cart;
