import React, { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import "./Favorite.css";

const Favorite = () => {
    const { getFavorite, favorite, deleteFromFavorite } = useContext(favoriteContext);
  const navigate = useNavigate();
  useEffect(() => {
    getFavorite();
  }, []);
    console.log(favorite);
    const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "24%", 
        marginBottom: "8px", 
        borderRadius: "none",
        position:"relative"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }

    const smallCard = {
        width: "286px",
        height: "374px",
        marginRight: "8px",
        width: "24%",
        position: "relative"
    }

    const smallCardPic = {
        width: "286px",
        height: "330px", 
    }
    return (
        <Card style={cardStyle} key={item.id} square={true}>
            renderItem={(item) => (
            <CardActionArea>
                <img className='favorite' src={require('../../images/favorite.png')} alt="little heart" />
            <CardMedia style={imageStyle}
                component="img"
                height="140"
                image={item.item.image}
                alt="hits image"
            />
            <CardContent style={cardContent}>
                <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                    {item.item.title}
                </Typography>
                <Typography style={priceStyle}  className='hit-price' variant="body2" color="text.secondary">
                    {item.item.price}
                </Typography>
                <Typography style={sizeStyle} variant="body2" color="text.secondary">
                    Размер : {item.item.size}
                </Typography>
                        <div className='colorful-circles'>
                            <div className='circle-blue'></div>
                            <div className='circle-green'></div>
                            <div className='circle-marron'></div>
                            <div className='circle-brown'></div>
                            <div className='circle-purple'></div>
                            <div className='circle-white'></div>
                            <div className='circle-grey'></div>
                            <div className='circle-pink'></div>
                        </div>
            </CardContent>
                </CardActionArea>
            )}
        </Card>
    );
};

export default Favorite;
