import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { useRef } from 'react';
import { cartContext } from '../../context/CartContext';

const Color = ({id}) => {
    const [colors, setColors] = useState([])
    const { isProdInCart, getColors } = useContext(cartContext)
    const [inCart, setInCart] = useState(isProdInCart(id))
    const show = useRef()
    useEffect(() => {
      axios.get("http://localhost:8000/colors")
        .then(response => {
            setColors(prev => [...prev, ...response.data])
        })
    }, [])
    console.log(inCart)
    console.log(id);
    // console.log(inCart[0]?.item?.color);


    const stateChange = () => {
        setShow(true)
    }




    return (
        <div>
            <div className='colorful-circles'>
                {colors.map((item) => {
                    let isShow = false

                    // console.log(item);
                    id == inCart[0]?.item?.id && inCart.map(i => {
                        if (i?.item.color == item.color) {
                            isShow = true
                        } else {
                            isShow = false
                        }
                    })

                    // item.color == inCart
                    return isShow ? <div key={item.id} className='border-circles'><div key={item.id} style={{ backgroundColor: item.color }} className='circle'></div>
                    </div>
                     : <div key={item.id} style={{ backgroundColor: item.color }} className='circle'></div>
                })}
            </div>
        </div>
    );
};

export default Color;
