import React, { useContext, useEffect, useState } from "react";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import "./Favorite.css";
import { favoriteContext } from "../../context/favoriteContext";
import { CardActionArea } from "@mui/material";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import FavoriteIcon from "@mui/icons-material/Favorite";
import axios from "axios";

const Favorite = () => {
    const { fav, getFav, deleteProdInFav, favoriteLength } = useContext(favoriteContext)
    const [extraProducts, setExtraProducts] = useState([])
    const[currentPage, setCurrentPage] = useState(1)
    const [fetching, setFetching] = useState(true)
    const [limit, setLimit] = useState(12)
    const newFav = []
    newFav = Object.entries(fav).slice(0,2)
    console.log(typeof fav)

  useEffect(() => {
    if (fetching) {
        getFav()
        setFetching(false)
        setCurrentPage()
        setLimit(prevState => prevState + 12)
        setCurrentPage(prevState => prevState + 1)
    }
  }, [fetching])

    useEffect(() => {
    document.addEventListener("scroll",scrollHandler)
    return function () {
        document.removeEventListener("scroll", scrollHandler)
    }
  }, [fetching])

  const scrollHandler = (e) => {
    if (e.target.documentElement.scrollHeight - (e.target.documentElement.scrollTop + window.innerHeight) < 100 &&  favoriteLength < 0) {
      setFetching(true)
    }
  }
     useEffect(() => {
      axios.get(`http://localhost:8000/summer?_limit=1`)
        .then(response => {
           setExtraProducts(prev => [...prev, ...response.data])
         })
     }, [])
    useEffect(() => {
      axios.get(`http://localhost:8000/new?_limit=1`)
        .then(response => {
           setExtraProducts(prev => [...prev, ...response.data])
         })
    }, [])
    useEffect(() => {
      axios.get(`http://localhost:8000/hits?_limit=1`)
        .then(response => {
           setExtraProducts(prev => [...prev, ...response.data])
         })
    }, [])
    useEffect(() => {
      axios.get(`http://localhost:8000/brandnew?_limit=1`)
        .then(response => {
           setExtraProducts(prev => [...prev, ...response.data])
         })
    }, [])
    useEffect(() => {
      axios.get(`http://localhost:8000/summer?_limit=1`)
        .then(response => {
           setExtraProducts(prev => [...prev, ...response.data])
         })
    }, [])


    const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "24%", 
        marginBottom: "8px", 
        borderRadius: "none",
        position:"relative"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }

    const smCardStyle = {
        width: "226px",
        height: "435px",
        marginRight: "8px",
        width: "20%", 
        marginBottom: "25px", 
        borderRadius: "none",
        position: "relative", 
        border: "none", 
        boxShadow:"none"
    }

    const smImageStyle = {
        width: "226px", 
        height: "332px"
    };

    return (
        <div className="fav-div">
            <div className="container">
                <h3 className='fav-main-title'>Избранное</h3>
                <div>
                    {fav?.products.length > 0 ? (
                        <>
                            <p className="fav-main-text">Товары в избранном 16</p>
                            <div style={{display:"flex", flexWrap:"wrap"}}>
                            {fav.products.map((item1) => (
                                <Card style={cardStyle} key={item1.item.id} square={true}>
                                    <CardActionArea>
                                        <FavoriteIcon
                                            className='favorite'
                                            style={{ color: "red" }}
                                            onClick={() => {
                                                deleteProdInFav(item1.item.id)
                                            }}
                                        />
                                        <CardMedia
                                            style={imageStyle}
                                            className="photos"
                                            height="140"
                                            component="img"
                                            image={item1.item.img}
                                            alt="fav image"
                                        />
                                        <CardContent style={cardContent}>
                                            <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                                                {item1.item.title}
                                            </Typography>
                                            <Typography style={priceStyle} className='hit-price' variant="body2" color="text.secondary">
                                                {item1.item.price}
                                            </Typography>
                                            <Typography style={sizeStyle} variant="body2">
                                                Размер : {item1.item.size}
                                            </Typography>
                                            <div className='colorful-circles'>
                                                <div className='circle-blue'></div>
                                                <div className='circle-green'></div>
                                                <div className='circle-marron'></div>
                                                <div className='circle-brown'></div>
                                                <div className='circle-purple'></div>
                                                <div className='circle-white'></div>
                                                <div className='circle-grey'></div>
                                                <div className='circle-pink'></div>
                                            </div>
                                        </CardContent>
                                    </CardActionArea>
                                </Card>
                            ))}
                        </div>
                        </>
                    ) : (
                            <>
                            <div>
                                <p className="favorite-text">У вас пока нет избранных товаров</p>
                                <h3 className="favorite-title">Возможно Вас заинтересует</h3>
                                </div>
                                <div style={{display:"flex"}}>
                                    {extraProducts.map((item) => (
                                        <Card style={smCardStyle} key={item.id} square={true}>
                                            <CardActionArea>
                                                <CardMedia
                                                    style={smImageStyle}
                                                    className="photos"
                                                    height="140"
                                                    component="img"
                                                    image={item.img}
                                                    alt="fav image"
                                                />
                                                <CardContent style={cardContent}>
                                                    <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                                                        {item.title}
                                                    </Typography>
                                                    <Typography style={priceStyle} className='hit-price' variant="body2" color="text.secondary">
                                                        {item.price}
                                                    </Typography>
                                                    <Typography style={sizeStyle} variant="body2">
                                                        Размер : {item.size}
                                                    </Typography>
                                                    <div className='colorful-circles'>
                                                        <div className='circle-blue'></div>
                                                        <div className='circle-green'></div>
                                                        <div className='circle-marron'></div>
                                                        <div className='circle-brown'></div>
                                                        <div className='circle-purple'></div>
                                                        <div className='circle-white'></div>
                                                        <div className='circle-grey'></div>
                                                        <div className='circle-pink'></div>
                                                    </div>
                                                </CardContent>
                                            </CardActionArea>
                                            </Card>
                                    ))}
                                </div>
                        </>
                    )}
                </div>
            </div>
        </div>

    );
};

export default Favorite;
