import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const SearchPage = () => {
    const { getAllProducts, data } = useContext(allContext)
    useEffect(() => {
        getAllProducts()
    }, [])
    const allData = data.reduce((acc, val) => acc.concat(val), [])
    const params = useParams()
    const searchItem = params.searchValue

    const newFilter = allData.filter((value) => {
            return value.title.toLowerCase().includes(searchItem.toLowerCase());
    });

    const [pic, setPic] = useState(item.img)
    const [hover, setHover] = useState("hover")

    const handleMouse = (event) => {
        var mouseX = event.nativeEvent.offsetX; 
        let cardWidth = event.target.clientWidth
        let specWidth = Math.ceil(cardWidth / 4)

        if (mouseX > 1 && mouseX < specWidth) {
            setPic(item.img)
            setHover("hover-1")
        }if (mouseX > specWidth && mouseX < specWidth * 2) {
            setPic(item.img2)
            setHover("hover-2")
        } else if (
            mouseX > specWidth * 2 && mouseX < specWidth * 3) {
            setPic(item.img3)
            setHover("hover-3")
        } else if (
            mouseX > specWidth * 3 && mouseX < specWidth * 4) {
            setPic(item.img4)
            setHover("hover-4")
             }
    }
    const handleLeave = () => {
        setPic(item.img)
        setHover("hover")
    }
    return (
        <div>
            {newFilter.map((item) => (
            <Card square={true} onMouseMove = {(e) => handleMouse(e)} onMouseLeave={()=>handleLeave()}> 
            <CardActionArea>
                 {inFav ? (
                <FavoriteIcon
                className='favorite'
                style={{ color: "red" }}
                onClick={() => {
                  addDelToFav(item);
                  setInFav(isProdInFav(item.id));
                }}
              />
            ) : (
                <FavoriteBorderIcon
                style={{ color: "white", width:"24px" }}
                className='favorite-hover'
                onClick={() => {
                  addDelToFav(item);
                  setInFav(isProdInFav(item.id));
                }}
              />
                )}
            {item.discount ? <div className='red-discount'><span>{item.discount}%</span></div>: null}
            <Link to={`/hitdetails/${item.id}`}>
                <CardMedia
                    component="img"
                    height="140"
                    image={pic}
                    alt="hits image"
                />
                </Link>
                <div className={hover}></div>
            <CardContent>
            <Typography gutterBottom variant="h5" component="div">
                {item.title}
            </Typography>
            {item.discount ?
                <div><span className='discount'>{discount} p</span><span className='price-discount'>{item.price} p</span></div> :  
                <Typography className='hit-price' variant="body2" color="text.secondary"><span className='discount'>{item.price} p</span>
                </Typography>
            }   
            <Typography variant="body2" color="text.secondary">
                Размер : {item.size}
            </Typography>
            <Color/>
            </CardContent>
            </CardActionArea>
        </Card>
            ))}
        </div>
    );
};

export default SearchPage;
