import React, { useContext, useEffect } from 'react';
import { headerContext } from '../../context/HeaderContext';

const Header = () => {
    const { getHeader, header } = useContext(headerContext)
    useEffect(() => {
        getHeader()
    }, [])
    return (
        <div>
            {header.map((item) => (
                <div key={item.id}>
                    <div>
                        <div>
                            <span></span>
                        </div>
                    <div>
                            <p>Тел: {item.number}</p>
                            </div>
                </div>
                    <img src={item.logo} alt="the logo" />
                    </div>

            ))}
        </div>
    );
};

export default Header;
