import React, { useContext, useEffect, useState } from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
import { summerContext } from '../../context/SummerCollection';
import "./Details.css"
import DetailsCard from './DetailsCard';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";

const Details = () => {
    const { getOneProduct, oneSummer } = useContext(summerContext)
    const [activeColor, setActiveColor] = useState("white");
    const { summer, getSummer } = useContext(summerContext)



    const params = useParams();
    useEffect(() => {
    getOneProduct(params.id);
    }, []);
    return (
        <div>
            {oneSummer ? (
                <DetailsCard id={params.id} item={oneSummer} />
            ) : <h1>Loading</h1>}
            <div>
                <p>Похожие товары</p>
                {summer.slice(0, 5).map((item) => (
                    <Card style={smCardStyle} key={item.id} square={true}>
            <CardActionArea>
                {inFav ? (
                    <FavoriteIcon
                    className='favorite'
                    style={{ color: "red" }}
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />
                ) : (
                    <FavoriteBorderIcon
                    style={{ color: "white" }}
                    className='favorite'
                    onClick={() => {
                    addDelToFav(item);
                    setInFav(isProdInFav(item.id));
                    }}
                />)}
                    <CardMedia
                        style={smImageStyle}
                        className="photos"
                        height="140"
                        component="img"
                        image={item.img}
                        alt="fav image"
                    />
                    <CardContent style={cardContent}>
                        <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                            {item.title}
                        </Typography>
                        <Typography style={priceStyle} className='hit-price' variant="body2" color="text.secondary">
                            {item.price}
                        </Typography>
                        <Typography style={sizeStyle} variant="body2">
                            Размер : {item.size}
                        </Typography>
                        <div className='colorful-circles'>
                            <div className='circle-blue'></div>
                            <div className='circle-green'></div>
                            <div className='circle-marron'></div>
                            <div className='circle-brown'></div>
                            <div className='circle-purple'></div>
                            <div className='circle-white'></div>
                            <div className='circle-grey'></div>
                            <div className='circle-pink'></div>
                        </div>
                    </CardContent>
                </CardActionArea>
                </Card>
                ))}
            </div>

        </div>
    )

};

export default Details;
