import React, { useContext, useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import { summerContext } from '../../context/SummerCollection';
import "./Details.css"
import DetailsCard from './DetailsCard';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import SummerCard from '../Summer/SummerCard';
    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }

    const smCardStyle = {
        width: "226px",
        height: "435px",
        marginRight: "8px",
        width: "20%", 
        marginBottom: "25px", 
        borderRadius: "none",
        position: "relative", 
        border: "none", 
        boxShadow:"none"
    }

    const smImageStyle = {
        width: "226px", 
        height: "332px"
    };

const Details = () => {
    const { getOneProduct, oneSummer } = useContext(summerContext)
    const [activeColor, setActiveColor] = useState("white");
    const { summer, getSummer } = useContext(summerContext)
    const params = useSearchParams()
    useEffect(() => {
    getSummer();
    }, []);
    useEffect(() => {
    getOneProduct(params.id);
    }, []);
    return (
        <div>
            {oneSummer ? (
                <DetailsCard id={params.id} item={oneSummer} />
            ) : <h1>Loading</h1>}
            <div>
                <p>Похожие товары</p>
                {summer.slice(0, 5).map((item) => (
                    <SummerCard item={item}/>
                ))}
            </div>

        </div>
    )

};

export default Details;
