import React, { useContext, useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import Pagination from '@mui/material/Pagination';
import { useSearchParams } from 'react-router-dom';
import { collectionContext } from '../../context/Collection';

const Collection = () => {
    const {collection, getCollection, collectionCount} = useContext(collectionContext)
    const [searchParams, setSearchParams] = useSearchParams();
    const [limit, setLimit]=useState(8)
    const [page, setPage]=useState(
    searchParams.get("_page") ? searchParams.get("_page") : 1
    );

  useEffect(() => {
    getCollection();
  }, []);

    useEffect(() => {
        setSearchParams({
      _page: page,
      _limit: 8,
    });
    }, [page]);

    useEffect(() => {
    getCollection();
  }, [searchParams]);

    const smallCard = {
        width: "286px",
        height: "374px",
        marginRight: "8px",
        marginBottom: "8px",
        width: "24%",
        position: "relative"
    }

    const smallCardPic = {
        width: "286px",
        height: "330px", 
    }
    return (
            <div className='container'>
                <h3 className='hit-main-title'>Хит продаж</h3>
                <div style={{display: "flex", flexWrap: "wrap"}}>
                    {collection.map((item) => (
                        <Card style={smallCard} key={item.id}>
                        <CardMedia style={smallCardPic}
                            component="img"
                            height="140"
                            image={item.img}
                            alt="image"
                        />
                            <div className='collection-words'>
                                <p>{item.title}</p>
                            </div>
                            <button className='collection-bottom'>Смотреть все
                                <ArrowForwardIosIcon sx={{ fill: '#fff'}} />
                            </button>
                    </Card>
                    ))}
            </div>
            <Pagination
                variant="outlined" shape="rounded"
                page={+page}
                count={+collectionCount}
                defaultPage={1}
                onChange={(page) => {
                    setPage(page);
                }}
            />
            </div>
    );
};

export default Collection;
