import React, { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';
import "./Favorite.css";
import { favoriteContext } from "../../context/favoriteContext";

const Favorite = () => {
    const { fav, getFav, deleteProdInFav } = useContext(favoriteContext)
  const navigate = useNavigate();
  useEffect(() => {
    getFav();
  }, []);
    const cardStyle = {
        width: "286px",
        height: "536px",
        marginRight: "8px",
        width: "24%", 
        marginBottom: "8px", 
        borderRadius: "none",
        position:"relative"
    }
    const imageStyle = {
        width: "286px", 
        height: "437px"
    };

    const titleStyle = { 
        fontSize: "14px",
        lineHeight: "17px",
        color: "#393939",
        margin: "6px 0"
    }
    const priceStyle = { 
        fontWeight: "500",
        fontSize: "16px",
        lineHeight: "20px",
        color: "#393939",
        margin: "0", 
        color:"black"
    }

    const cardContent = {
        padding: "6px 8px"
    }

    const sizeStyle = { 
        fontSize: "13px",
        lineHeight: "16px",
        color: "#7C7C7C",
        margin: "6px 0"
    }

    const smallCard = {
        width: "286px",
        height: "374px",
        marginRight: "8px",
        width: "24%",
        position: "relative"
    }

    const smallCardPic = {
        width: "286px",
        height: "330px", 
    }
    return (
        <div className="favoriteCont">
        {fav?.products.length > 0 ? (
          fav.products.map((item1) => (
            <Card
              key={item1.item.id}
            style={cardStyle}
            >
                <CardMedia
                style={{imageStyle}}
                className="photos"
                height="140"
                component="img"
                image={item1.item.image}
                alt="fav image"
              />
              <CardContent >
                <Typography style={titleStyle} gutterBottom variant="h5" component="div">
                            {item1.item.title}
                        </Typography>
                        <Typography style={priceStyle}  className='hit-price' variant="body2" color="text.secondary">
                            {item1.item.price}
                        </Typography>
                        <Typography style={sizeStyle} variant="body2" color="text.secondary">
                            Размер : {item1.item.size}
                        </Typography>
              </CardContent>
            </Card>
          ))
        ) : (
          <h1>No products</h1>
        )}
      </div>

    );
};

export default Favorite;
