import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { allContext } from '../../context/AllContext';

const SearchPage = () => {
    const { getAllProducts, data } = useContext(allContext)
    useEffect(() => {
        getAllProducts()
    }, [])
    const allData = data.reduce((acc, val) => acc.concat(val), [])
    const params = useParams()
    const searchItem = params.searchValue

    const newFilter = allData.filter((value) => {
            return value.title.toLowerCase().includes(searchItem.toLowerCase());
    });

    const [pic, setPic] = useState(item.img)
    const [hover, setHover] = useState("hover")

    const handleMouse = (event) => {
        var mouseX = event.nativeEvent.offsetX; 
        let cardWidth = event.target.clientWidth
        let specWidth = Math.ceil(cardWidth / 4)

        if (mouseX > 1 && mouseX < specWidth) {
            setPic(item.img)
            setHover("hover-1")
        }if (mouseX > specWidth && mouseX < specWidth * 2) {
            setPic(item.img2)
            setHover("hover-2")
        } else if (
            mouseX > specWidth * 2 && mouseX < specWidth * 3) {
            setPic(item.img3)
            setHover("hover-3")
        } else if (
            mouseX > specWidth * 3 && mouseX < specWidth * 4) {
            setPic(item.img4)
            setHover("hover-4")
             }
    }
    const handleLeave = () => {
        setPic(item.img)
        setHover("hover")
    }
    return (
        <div>
            {newFilter.map((item) => (
                <SearchPage item={item} key={item.id}/>
            ))}
        </div>
    );
};

export default SearchPage;
